<?php if (mysqli_num_rows($result_faq) > 0) { ?><style>.faqs_box{background:#fff;color:#000;}.accordion .accordion-item{border-bottom:1px solid #e5e5e5;}.accordion button[aria-expanded='true']{border-bottom:1px solid #0A7F17;}.accordion button{position:relative;display:block;text-align:left;width:98%;padding:20px 10px;border:none;color:#000;background:none;outline:none;}.accordion button:hover,.accordion button:focus{cursor:pointer;color:#0A7F17;}.accordion button:hover::after,.accordion button:focus::after{cursor:pointer;color:#0A7F17;border:1px solid #0A7F17;}.accordion-title{padding:1em 1.5em 1em 0;font-size:16px;}.icon{display:inline-block;position:absolute;top:18px;right:3px;width:22px;height:22px;border:1px solid;border-radius:22px;}.icon::before{display:block;position:absolute;content:'';top:9px;left:5px;width:10px;height:2px;background:currentColor;}.icon::after{display:block;position:absolute;content:'';top:5px;left:9px;width:2px;height:10px;background:currentColor;}.accordion button[aria-expanded='true']{color:#0A7F17;}.accordion button[aria-expanded='true'] .icon::after{width:0;}.accordion button[aria-expanded='true']+.accordion-content{opacity:1;max-height:none;transition:all 200ms linear;will-change:opacity,max-height;}.accordion-content{opacity:0;max-height:0;overflow:hidden;transition:opacity 200ms linear,max-height 200ms linear;will-change:opacity,max-height;}.accordion-content p{margin:2em 30px 2em 0px;color:#000;text-align:justify;}</style><div class="row content-block dark"><div class="date-chooser flex-grow-1"><h3>&#10042; FAQ's</h3></div></div><div class="faqs_box"><div class="container"><div class="clear"></div><div class="accordion"><?php while ($row_faq = mysqli_fetch_array($result_faq)) { ?><div class="accordion-item"><button id="accordion-button-<?= $row_faq['id'] ?>" aria-expanded="false"><span class="accordion-title"><?= $row_faq['question'] ?></span><span class="icon" aria-hidden="true"></span></button><div class="accordion-content"><p><?= $row_faq['answer'] ?></p></div></div><?php } ?></div></div></div><div style="clear: both; margin: 10px;"></div><?php } ?>
